void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 stars = texture(iChannel0, uv *.1).xyz;

    vec3 col = vec3(1.) - stars*.1;
    col += .8* texture(iChannel1, -uv + vec2(cos(iTime*.4),sin(iTime))*.2).xxx;
    col += .6* texture(iChannel1, uv + vec2(cos(iTime*.4),sin(iTime))*.2).xxx;


    col *= vec3(stars.x*1.2, stars.y*1.6, stars.z*3.);
    col *= .9;
    fragColor = vec4(col,1.0);
}
