// Fork of "Magic bubbles" by ChunderFPV. https://shadertoy.com/view/Mf23W1
// 2024-08-31 05:18:58

// radial blur
#define H(a) (cos(radians(vec3(0, 60, 120))+(a)*6.2832)*.5+.5)  // hue
#define T(p) texture(iChannel0, mix(u, m, p-i*p), a).rgb // scale texture
void mainImage( out vec4 C, in vec2 U )
{
    vec2 R = iResolution.xy,
         m = iResolution.xy/R*R,
         u = U/R;
    m = vec2(.5);

    vec3 c = texture(iChannel0, u).rgb, k;

    float loop = 60.,  // loop size
          increment = 1./loop, // increment size
          a = length((U+U-R)/R.x*3.), // mipmap aa
          brightness = increment*2.,   // brightness
          i = increment;

    for (; i<=1.; i+=increment)
          k = T(12.) + T(-1.), // blur both ways
          c += brightness * sqrt(i) * H(i-iTime/5.) * (k + .1 - i*.2); // light, color, shadow

    c.r += .65-a*.21;
    C = vec4(tanh(c*c), 1);
}

// Fork of "Fibonacci - 1" by ChunderFPV. https://shadertoy.com/view/dd2yWw
#define A(a) mat2(cos(a+vec4(0, -1.5708, 1.5708, 0))) // rotate
void mainImage( out vec4 C, in vec2 U )
{
    float scale = 2.3,  // scale
          t = iTime/10.,
          i = 0., h = i, r;

    vec2 R = iResolution.xy,
         m = (iResolution.xy-R/6.5)/R.y*scale,
         u = (U-R/2.)/R.y*scale;


        m = sin(t*6.2832 + vec2(1, 1.5708)), // move in circle
        m.x *= 7.058; // stretch x

    t = pow(t, 2.3);
    vec3 c = vec3(0, 1, 2)/20.*max(1., 1.-length(u)); // background
    c += min(1., .01/length(u-m)); // mouse light

    for (; i++< 50.;)
    {
        r = length(u);
        r = min(2., .02/abs(3.-r)); // rings
        c -= r*.02 + r*h*1.3; // shadow
        mat2 a = A(sqrt(5.*i*t*3.1416)); // rotation
        r = length(u/max(0., 1.272-dot(u, u))-m); // transform
        u *= a*1.618; // rotate & scale
        m *= a*0.618;
        u.x = abs(u.x)-1.272; // shift
        m.x = abs(m.x)-1.272;
        h = min(1., .11/r)*.5;
        c += h; // directional lights
        c += min(1., .0113/length(u) + .006/length(abs(u)-vec2(0, 11))); // points
    }

    C = vec4(c*1.2, 1);
}
