#define R3D(a) mix(q*dot(p,q),p,cos(a))+sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-1,2,1), r=iResolution;
for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=.3;
    p=R3D(t*.2)*mod(t,i);

    for(j=0.;j++<16.;)
        p=abs(abs(p-.2)-.05)-.05,
        d=max(max(p.z,p.y),p.x),
        d-=dot(p,q)/1e4;

    e+=d;

}o+=vec4(exp(q*p/e),1)*3./i;
}
