#define PI 3.14159265359

// Function to create a circular mask
float circularMask(vec2 uv, float radius) {
    float dist = length(uv);
    return smoothstep(radius - 0.01, radius + 0.01, dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize and center coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Define time for motion control
    float time = iTime * 0.99;

    // Parameters for the diamond patterns
    float frequencyOuter = 20.0;
    float thicknessOuter = 0.60;
    float directionOuter = -1.0; // 1.0 for outward, -1.0 for inward

    float frequencyInner = 22.0;
    float thicknessInner = 0.60;
    float directionInner = 1.0; // 1.0 for outward, -1.0 for inward

    // Calculate the diamond pattern outside the circular mask
    float outerPattern = abs(sin(frequencyOuter * (abs(uv.x) + abs(uv.y)) + directionOuter * time * 10.0));
    outerPattern = smoothstep(thicknessOuter, thicknessOuter + 0.20, outerPattern);

    // Calculate the diamond pattern inside the circular mask
    float innerPattern = abs(sin(frequencyInner * (abs(uv.x) + abs(uv.y)) + directionInner * time * 10.0));
    innerPattern = smoothstep(thicknessInner, thicknessInner + 0.20, innerPattern);

    // Create the circular mask
    float circle = circularMask(uv, 0.37);

    // Combine the patterns based on the circular mask
    float finalPattern = mix(innerPattern, outerPattern, circle);

    // Output the final color
    fragColor = vec4(vec3(finalPattern), 1.0);
}
