void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y;
    uv.x *= iResolution.x / iResolution.y;

    uv -= 0.5;
    uv *= 2.0;
    uv = abs(uv);

    vec3 col = vec3(step(0.01, uv.x) * step(0.01, uv.y));
    col = 1.0 - col;
    if (uv.x > 0.05) col = vec3(0.0);
    if (uv.y > 0.05) col = vec3(0.0);

    if (uv.x > 1.0) col = vec3(0.0);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
