void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ChromaAmount = .1f;

    vec2 uv = fragCoord/iResolution.xy;

    vec2 coords = uv * 2.f - 1.f;
    vec2 end = uv - coords * dot(coords, coords) * ChromaAmount;
    vec2 delta = (end - uv) / 3.0;

    float r = texture(iChannel0, uv).x;
    float g = texture(iChannel0, delta + uv).y;
    float b = texture(iChannel0, delta * 2.0 + uv).z;

    vec3 color = vec3(r, g, b);
    fragColor = vec4(color, 1.f);
}
