void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/min(iResolution.x, iResolution.y);

    vec4 o_col = vec4(0.);
    float n_its = 15.;
    float n_it_nor_one = 1./n_its;
    float n_tau = 6.2831;
    for(float n_it_nor = 0.; n_it_nor < 1.; n_it_nor +=n_it_nor_one){

        vec4 oan = texture(iChannel0, vec2(n_it_nor));
        float n_t = iTime*3.;
        vec2 o_p = o_trn - vec2(
            sin(oan.x*n_tau+n_t*oan.y),
            cos(oan.y*n_tau+n_t*oan.x)
        )*vec2(oan.xz)*.5;
        vec4 o_c2 = vec4(0.);
        float n_radius = 0.03;
        float n = length(o_p)-n_radius;
        //n = smoothstep(0.8, 0.9, n);
        //n = clamp(n, 0., 1.);
        n = abs(n);
        n = 1.-pow(n, 1./(n_its*.7));
        //o_c2[int(oan.w*4.)] = n;
        o_c2 = n * oan.xzwy;
        o_col += o_c2;
    }

    // Output to screen
    fragColor = vec4(o_col);
}
