vec3 palette(float t) {
    vec3 apal = vec3(0.5, 0.5, 0.5);
    vec3 bpal = vec3(0.5, 0.5, 0.5);
    vec3 cpal = vec3(1.0, 1.0, 1.0);
    vec3 dpal = vec3(0.26, 0.42, 0.56);

    return apal + bpal*cos( 6.28318*(cpal*t + dpal) );
}

vec2 cMult(vec2 z, vec2 w) {
    return vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
}
vec2 cAdd(vec2 z, vec2 w) {
    return vec2(z.x + w.x, z.y + w.y);//Necessary??
}
float cMod(vec2 z) {
    return sqrt(z.x * z.x + z.y * z.y);
}

//an option
//vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //setting up coordinate system
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec2 z0 = vec2(0.67532475,0.43769101);

    uv = (uv + z0) / pow(1.5,iTime) - z0;
    vec2 c = uv;
    vec2 z = uv;
    //defining variables to be changed later
    vec3 finalColor = vec3(0.0);

    float itter = 10.0 + 4.*iTime + pow(iTime/4.,2.0);
    //first bit of logic
    for (float i = 0.0; i < itter; i++) {

    z = cMult(z,z) + c;

    }

    //finalColor = palette(cMod(z-c));
    finalColor = vec3(abs(z.x), cMod(z-c), abs(z.y));

    //end
    fragColor = vec4(finalColor, 1.0);
}
