#define PI 3.14159265359

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    // Calculate distance from center
    float dist = length(uv);

    // Create anti-aliased circle
    float circleRadius = 0.43;
    float edgeSmoothing = 0.0015; // Adjust this value to control the amount of anti-aliasing
    float circle = smoothstep(circleRadius, circleRadius - edgeSmoothing, dist);

    // Auto-invert based on time
    float invert = step(sin(iTime * 50.0), 0.0);

    // Define colors
    vec3 gray = vec3(0.16078); // #292929 in RGB
    vec3 white = vec3(1.0);
    vec3 black = vec3(0.0);

    // Choose colors based on inversion state
    vec3 circleColor = invert == 1.0 ? gray : white;
    vec3 bgColor = invert == 1.0 ? white : black;

    // Combine circle and background
    vec3 finalColor = mix(bgColor, circleColor, circle);

    // Output color
    fragColor = vec4(finalColor, 1.0);
}
