// 2024年8月31日
// 第3种五边形平铺

float dln(in vec2 a, in vec2 b, in vec2 p) {
        b -= a;
        p -= a;
        float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
        return length(p - b * h);
}

float hd(vec2 p) {
        p -= vec2(-1, 0);
        p = abs(p);
        float m = 1e8, h = .25+.25*sin(iTime-10.); // 0. < h < 0.5
        if(iMouse.x>iResolution.x*.55) h = .3;
        vec2 a = vec2(0),
                b = vec2(0, h),
                c = vec2(1, 1. - h),
                d = vec2(1.5, .5),
                e = vec2(1. + h, 0);
        m = min(m, dln(a, b, p));
        m = min(m, dln(c, b, p));
        m = min(m, dln(c, d, p));
        m = min(m, dln(e, d, p));
        m = min(m, dln(e, a, p));
        return m;
}

float pentagon03(vec2 p) {
        float d = 1e8;
        vec2 k = vec2(3, 1),
                s = vec2(-1, 1),
                f = fract(p / k) * k;
        if (f.x > 1.5) f = vec2(3, 1) - f;
        f = (f + f.yx * s);
        d = min(d, hd(f - vec2(0, 1)));
        d = min(d, hd(f - vec2(1, 0)));
        d = min(d, hd((f - vec2(1, 1)).yx * s));
        d = min(d, hd((f - vec2(0, 2)).yx * s));
        return d;
}

void mainImage(out vec4 O, vec2 U) {
        O = vec4(.5);
        vec2 R = iResolution.xy,
                u = 3. * (U + U - R) / R.y;
        O = .8 - vec4(.005 / pentagon03(u));
}
