vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);
    return a + b * cos(6.28318 * (c * t + d));
}

float plasma(vec2 uv) {
    float v = 0.0;
    v += sin((uv.x + iTime) * 10.0);
    v += sin((uv.y + iTime) * 10.0);
    v += sin((uv.x + uv.y + iTime) * 10.0);
    v += sin(sqrt(uv.x * uv.x + uv.y * uv.y + 1.0) * 10.0);
    return v / 4.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float plasmaValue = plasma(uv);
    vec3 plasmaColor = palette(plasmaValue);

    // Add metallic effect
    float metallic = pow(1.0 - abs(dot(normalize(vec3(uv, 1.0)), vec3(0.0, 1.0, 0.0))), 2.0);
    vec3 finalColor = mix(plasmaColor, vec3(1.0), metallic * 0.5);

    fragColor = vec4(finalColor, 1.0);
}
