const float kernel = 3.0;
const float weight = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 sum = vec3(0);
    float pixelSize = 1.0 / iResolution.x; 

    // Horizontal Blur
    vec3 accumulation = vec3(0);
    vec3 weightsum = vec3(0);
    for (float i = -kernel; i <= kernel; i++){
        accumulation += texture(iChannel0, uv + vec2(i * pixelSize, 0.0)).xyz * weight;
        weightsum += weight;
    }

    sum = accumulation / weightsum;

    fragColor = vec4(sum, 1.0);
}

float hash(in vec2 uv)
{
    float seed = dot(uv, vec2(71.33, 852.381));
    return fract(38351.43*sin(14.13*seed));
}

mat4 saturationMatrix( float saturation )
{
    vec3 luminance = vec3( 0.3086, 0.6094, 0.0820 );
    float oneMinusSat = 1.0 - saturation;
    vec3 red = vec3( luminance.x * oneMinusSat );
    red+= vec3( saturation, 0, 0 );
    vec3 green = vec3( luminance.y * oneMinusSat );
    green += vec3( 0, saturation, 0 );
    vec3 blue = vec3( luminance.z * oneMinusSat );
    blue += vec3( 0, 0, saturation );

    return mat4( red,     0,
                 green,   0,
                 blue,    0,
                 0, 0, 0, 1 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord / iResolution.xy;

    //chromatic abberation
    vec2 d = abs((uv - 0.5) * 2.0);
    d = pow(d, vec2(2.0, 2.0));

    vec4 r = texture(iChannel0, uv - d * 0.015);
    vec4 g = texture(iChannel0, uv);
    vec4 b = texture(iChannel0, uv);

    fragColor = vec4(r.r, g.g, b.b, 1.0);

        //add noise
    uv+=hash(uv*iTime);
    fragColor += hash(uv)*0.38; 


      //add VHS
    float u_speed = .5; 
    float stripePos = mod(uv.y +  iTime * u_speed, 1.0);
    float streakAmount = smoothstep(0., 0.002, abs(stripePos - uv.y));
    float trackingLinePos = mod(iTime * 0.1, 1.0); 
    streakAmount += smoothstep(0.5, 0.02, abs(trackingLinePos - uv.y)) * hash(vec2(trackingLinePos, uv.x))*.4; 
        fragColor*=streakAmount;

    //change saturation
    float saturation = .93;
    fragColor*= saturationMatrix( saturation );


}

