float sdP( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528); // pi/5: cos, sin, tan
    p.y = -p.y;
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
	p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

float sdH( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float sdO( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.9238795325, 0.3826834323, 0.4142135623 );
    p = abs(p);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;

    uv.x*=iResolution.x/iResolution.y;

    vec2 uv0=uv;

    uv.x+=sin(uv.y*sin(iTime)*10.+iTime)/(sin(iTime)*10.);

    uv.y+=cos(uv.x*cos(iTime)*10.+iTime)/(cos(iTime)*10.);

    float x=sdP(uv,2.);

    x=sin(x*8.+iTime)/8.;

    x=abs(x);

    x=pow(0.01/x,1.2);

    uv.x+=sin(uv.y*x*10.+iTime)/(x*10.);

    float y=sdH(uv,2.);

    y=cos(y*8.+iTime*1.2571)/8.;

    y=abs(y+x);

    y=pow(0.01/y,5.*x);

    uv.y-=cos(uv.x*y+iTime)/y;

    float z=sdO(uv,round(x/y)/10.);

    z=sin(z*8.+iTime*0.7967+1.)/8.;

    z=abs(z)*y;

    z=pow(0.01/z,1.2+y);

    vec3 col = vec3(x,y,z);

    fragColor = vec4(col,1.0);
}
