Main {
    if (U.y>2.) discard;
    vec4 p[N];
    vec4 v[N];
    for (int i = 0; i < N; i++) {
        p[i] = A(.5+vec2(i,0));
        v[i] = A(.5+vec2(i,1));

    }
    for (int k = 0; k < min(50,50+iFrame); k++) 
    for (int i = 0; i < min(N,N+iFrame); i++) {
        vec2 f = vec2(0);
        for (int j = 0; j < min(N,N+iFrame); j++) if (i!=j) {

            vec2 r = p[j].xy-p[i].xy;
            float l = length(r);
            f -= .1*p[i].w*p[j].w*r/l/l*(1.-exp(-.2*l));
            f -= 1.*abs(p[i].w*p[j].w)*r/l*(exp(-.2*l));
            f += 1.*(v[j].xy-v[i].xy)*pow(dot(normaliz(v[j].xy-v[i].xy),r/l),1.)/l/l;
        } 
        v[i].xy += f/p[i].z;
        p[i].xy += f/p[i].z+v[i].xy*inversesqrt(1.+dot(v[i].xy,v[i].xy));
    }


    for (int i = 0; i < N; i++) {
        if (U.y < 1. && int(U.x-.5)==i) Q = p[i];
        if (U.y > 1. && int(U.x-.5)==i) Q = v[i]; 
    }


    if (iFrame < 1) {

        Q = vec4(.5*R,1,-1);
        int b = int(U.x)/(M+1);
        U.x = mod(U.x,float(M+1));
        if (U.x < 1.) {
            Q.x += 0.;
            Q.z = 200.*float(M);
            Q.w = float(M);
        } else if (U.x < float(M+1)) {
            Q.xy += vec2(30,0)*ei(2.*pi*U.x/float(M));
        } 


        if (int(U.y)%2==1) {
            Q = vec4(Q.y-.5*R.y,.5*R.x-Q.x,0,0)/1000.;
        } else 
        {
            Q.x -= .2*R.x;
            Q.x += .2*R.x*float(b);
         }



    }


}

Main {

    Q = vec4(0);
    for (int i = 0; i < N; i++) {
        vec4 a = A(.5+vec2(i,0));
        Q += abs(a.w)*exp(-.5*length(a.xy-U));
        Q.w += a.w/length(a.xy-U);
    }


}
