#define NUM_EXPLOSIONS 5.
#define NUM_PARTICLES 50.

vec2 random (float t) {
    float x = fract (sin(t * 463.7) * 322.2);
    float y = fract ((sin(t*x) * 322.7) * 322.2);
    return vec2(x, y); }

vec2 random_polarcords (float t) {
    float a = fract (sin(t * 993.7) * 322.2)*2.0*3.14159265;
    float b = fract ((sin(t*a) * 993.7) * 322.2);
    return vec2(sin(a), cos(a)) * b; }

float Explosion(vec2 uv, float t) {
    float sparks = 0.0;
    for (float i = 0.0; i<NUM_PARTICLES; i++) {
        vec2 dir = random_polarcords(i+1.0)*0.96;   
        float d = (length(uv-dir*t));
        float light = min(0.7, mix(0.0005, 0.0005, smoothstep(0.05, 0.0, t)));
        light *= sin(t*20.0+i)*0.5 + 0.5;
        light *= smoothstep(1.0, 0.8, t);
        sparks += light/d; } return sparks; }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);
    for (float i = 0.0; i<NUM_EXPLOSIONS; i++) {
        float t = iTime+i/NUM_EXPLOSIONS;
        float ft = floor(t);
        vec3 color = cos(50.*vec3(0.36, 0.43, 0.35)+ft)*0.25+0.45; 
        vec2 of = random(i+1.0+ft)-0.5;
        of *= vec2(1.0 * (iResolution.x/iResolution.y), 0.95);
        col += Explosion(uv - of, fract(t))*color; }
    col *= 2.0;
    fragColor = vec4(col,1.0); }
