//This is me following a tutorial by Kishimisu. https://www.youtube.com/watch?v=khblXafu7iA
//I also followed a tutorial by Peer Play on how to add lighting https://www.youtube.com/watch?v=t8qBCltSqV4&list=PL3POsQzaCw53iK_EhOYR39h1J9Lvg-m-g&index=4
//Got help from https://www.shadertoy.com/view/tlKSzK

float minDist = 0.0001;
float maxDist = 100.;
int maxIter = 256;
vec3 lightDir = normalize(vec3(0., -1., 0.));
vec3 lightCol = vec3(1.,1.,1.);
float lightIntensity = 1.2;
float shadowIntensity = 1.;
float penumbra = 1.;
float aoStepSize = 0.1;
float aoIntensity = .1;
int aoIter = 4;
float reflectionIntensity = 0.3;
int reflectionIter = 4;

float sunion(float a, float b, float k){ //Boolean Union
    float h = max(k - abs(a-b), 0.0) / k;
    return min(a, b) - h*h*h*k*(1./6.);
} //returns the smooth minimum between two values (this is black magic too)

float sintersect(float a, float b, float k){ //Boolean Intersection
    return -sunion(-a, -b, k);
}

float ssub(float a, float b, float k){ //Boolean Subtract, first object minus second
    return -sunion(a, -b, k);
}

mat2 rot2d(float angle){ //2d rotation matrix, because the third rotation axis is more or less useless.
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float sdfSphere(vec3 p, float r){
    return length(p) - r; //distance to a sphere with radius r
}

float sdfBox(vec3 p, vec3 b) { //distance to a box with dimensions b (this function is basically black magic just copy and paste this part)
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float map(vec3 p) { //distance to scene

    vec3 spherePos = vec3(sin(iTime) * 3., 0, 0);
    float sphere = sdfSphere(p - spherePos, 1.);

    vec3 q = p;
    q.xz *= rot2d(iTime);
    float box = sdfBox(q, vec3(0.75));


    return sunion(sphere, box, 2.); //returns the closer distance between the sphere and box
}

vec3 getNormal(vec3 p){ //some black magic that gets the normal vector of the scene at a given point
    vec2 offset = vec2(minDist, 0.0);
    vec3 n = vec3(
    map(p + offset.xyy) - map(p - offset.xyy),
    map(p + offset.yxy) - map(p - offset.yxy),
    map(p + offset.yyx) - map(p - offset.yyx));
    return normalize(n);
}

float march(vec3 ro, vec3 rd){
    float t=0.,d;
    for(int i=0; i<maxIter; ++i){
        d=map(ro+rd*t);
        if(abs(d)<minDist) return t;
        t+=d;
        if(t>maxDist) return t;
    }
    return t;
}

float reflection(vec3 ro, vec3 rd){
    float t = march(ro, rd);
    if(t > maxDist) return t;
    vec3 p = ro + rd * t;
    vec3 n = getNormal(p);
    rd = reflect(rd, n);
    ro = p + rd*minDist;
}

float softShadow(vec3 ro, vec3 rd, float k) { //calculates whether a point is in shadow or not, but smooth
    float result = 1.;
    for(float t = minDist; t < maxDist;) {
        float h = map(ro+rd*t);
        if(h < minDist*.01){
            return 0.0;
        }
        result = min(result, k*h/t);
        t += h;
    }
    return result;
}


float ambientOcclusion(vec3 p, vec3 n){ //This is the point where I stop knowing how things work, I just know that they do.
    float aoStep = aoStepSize;
    float ao = 0.0;
    float dist;
    for(int i = 1; i <= aoIter; i++){
        dist = aoStep * float(i);
        ao += max(0.0, (dist - map(p + n * dist)) / dist);
    }
    return (1. - ao * aoIntensity);
}

vec3 shading(vec3 ro, vec3 rd, float t){
    vec3 p = ro + rd * t;
    vec3 n = getNormal(p);

    //Directional Light
    vec3 light = (lightCol * dot(-lightDir, n) * .5 + .5) * lightIntensity;
    //Shadows
    float shadow = softShadow(p, -lightDir, penumbra) * 0.5 + 0.5;
    shadow = max(0.0,pow(shadow, shadowIntensity));
    //Ambient Occlusion
    float ao = ambientOcclusion(p, n);


    light *= shadow * ao;

    return light;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y; // Screen coordinates (center = (0, 0))
    vec2 m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y; // Mouse coordinates
    float fov = 1.;

    //Initialization
    vec3 ro = vec3(0., 0., -3.); //ro stands for ray origin, think of it as camera position
    vec3 rd = normalize(vec3(uv * fov, 1.)); //rd stands for ray direction
    vec3 col = vec3(0.);

    float t = 0.; //total distance travelled


    //vertical camera rotation
    ro.yz *= rot2d(-m.y);
    rd.yz *= rot2d(-m.y);

    //horizontal camera rotation
    ro.xz *= rot2d(-m.x);
    rd.xz *= rot2d(-m.x); //It is important that vertical rotation comes first


    //Marching
    t = march(ro, rd);
    if(t < maxDist) { //shading
        vec3 s = shading(ro, rd, t);
        col = vec3(s);
    }     
    else {
        col = texture(iChannel0, rd).rgb;
    }


    //Reflections
    vec3 refCol;
    for(int i; i <= reflectionIter; i++){
        t=march(ro,rd);
        if(t > maxDist) {refCol = texture(iChannel0, rd).rgb;
        break;}
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        rd = reflect(rd, n);
        ro = p + rd * minDist;
        if(t < maxDist && i == reflectionIter) { //shading
            vec3 s = shading(ro, rd, t);
            refCol = vec3(s);
        }
        else {
            refCol = texture(iChannel0, rd).rgb;
        }
    }
    col = (1. - reflectionIntensity) * col + reflectionIntensity * refCol;
    fragColor = vec4(col, 1.);
}
