vec3 palette(float t){

    vec3 a = vec3(0.5, 0.0, 0.0);
    vec3 b = vec3(0.5, 0.0, 0.0);
    vec3 c = vec3(0.5, 0.0, 0.5);
    vec3 d = vec3(0.0, 0.0, 0.5);


    return a+b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.);



    for(float i = 1.; i < 8.; i++){ //For loop to make it look more interesting

        uv = 1.3*uv; //Changing the 1.5 leads to different patterns
        float d = cos(uv.x + i / 5.)*sin(uv.y - i / 8.) * exp(-.2*length(uv0)) * sin(length(uv)); //Changing this leads to new patterns
        vec3 col = palette(length(uv0) + iTime*.3 + i*.4); //Palette pattern

        d = d*10. + iTime;
        d = i*(sin(d) - cos(d)); //This also changes the pattern if you didn't see the trend
        d = abs(d) / 5.;

        d = pow(.01/d,0.6); //Making the contrast higher (1 for default, >1 for higher contrast)
        finalColor += col * d;
    }


    fragColor = vec4(finalColor,1.);
}
