#define pi (3.141592653589)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord) - iResolution.xy / 2.0;
    uv = uv / iResolution.xx;
    uv *= 5.0;
    vec3 baseMask = vec3(1.0,1.0,1.0);

    float time = 1.0 * iTime;

    float radius = 1.5;
    float depth = 2.0;
    float childs = 10.0;
    float breadth = pow(childs,depth);
    float circRad = radius / pow(2.0,depth);

    vec2 nearestCent = vec2(-100.0,-100.0);
    for(float i = 0.0; i < breadth; i++){

        vec2 centPos = vec2(0.0,0.0);

        for(float l = depth - 1.0; l >= 0.0; l--){

            float k = pow(childs,l);
            float per = floor(float(int(i) / int(k)));

            float y = pow(2.0, -(l + 1.0)) * radius * sin((l + 1.0) * time + 2.0 * pi * per / childs);
            float x = pow(2.0, -(l + 1.0)) * radius * cos((l + 1.0) * time + 2.0 * pi * per / childs);
            centPos += vec2(x,y);
        }

        if(distance(centPos,uv) < distance(nearestCent,uv)) nearestCent = centPos; 

    }


    /*vec3 col = vec3(0.0,0.0,0.0);

    if(distance(uv,nearestCent) < circRad) col = vec3(1.0,1.0,1.0);
    //else discard;
    // Output to screen
    fragColor = vec4(col,1.0);*/
    vec3 col = vec3(0.0,0.0,0.0);

    //if(distance(uv,nearestCent) < 4.0 * circRad) col = vec3(1.0,1.0,1.0);
    col = vec3(1.0,1.0,1.0) * tan(min(distance(nearestCent,uv),abs(sin(time))));
    col = sin(col);


    col *= vec3(0.0,sin(time),sin(col * 10.0));
    col /= distance(uv,vec2(0.0,0.0));
    //col /= 2.0 * (distance(nearestCent,uv) + 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
