#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//formulas for creating colors;
#define H(h)  (  cos(  h*3. + vec3(1,2,3) )*.5 + .3 )

#define H2(h)  (  cos(  h + vec3(1,2,4) )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution

void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, 2.*R.y))*200.;

    float sc,dotp,totdist=0., tt=iTime/3., t=0.;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p.z  -= 10. + min(1000.,100.*mod(tt,15.));

        p.xz *= rot( tt/2. + abs(sin(tt/3.)));

        p.yzw = p.xyz;

        sc = 1.; 

        p.zw *= rot(1.);

        vec4 w = p;

        for (float j=0.; j<8.; j++) {

            p = abs(p)*.75 +  .03*cos(p/4.) -  .6*sin(2.*p);

            dotp = max(1./dot(w,w),.5);
            sc *= dotp; 

            p = p * dotp - .25; 

            w = .8*log(1.+log(1.+log(1.+p)*p)*p)*p - vec4(0,0,0,1);


        }

        float dist = abs( length(p)-.02)/sc ;
        float stepsize = dist/150. ;     
        totdist += stepsize;    

        c +=
             .03 * H2(  atan( p.w, p.z )) * exp(-totdist/20.)
           + .02 * mix( vec3(1), H(M(sc)),.95) *  exp(-i*i*stepsize*stepsize/2.);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
