// variant of https://shadertoy.com/view/MfyBRR
// variant of "wireframe dodecahedron 3" https://shadertoy.com/view/4XfcR8


#define rot(a) mat2(cos(a+vec4(0,33,11,0)))
#define F(A)   dot(q,A) < 0. ?  q = reflect(q,A) : q 

void mainImage( out vec4 O, vec2 u ) {

    vec3 R = iResolution,
         P = vec3(0, 0, 2.5), q,
         D = normalize(vec3( u - .5*R.xy, -R.y )),
         M = iMouse.z > 0. ? iMouse.xyz/R -.5 :           // comment for no mouse cam
                 vec3( .15,.07,0) * cos(.3*iTime + vec3(0,11,0)),
         C = vec3(-.81,-.47, .36);

    float h=1.,c, i;
    for ( O=vec4(1); O.x > 0. && h > .01; O-=.01 ) {      // --- march scene
        q = P,
        q.yz *= rot(6.*M.y),                              // rotations
        q.xz *= rot(6.*M.x);
                                                          //  --- dodecahedron edges  
        for (int i; i++ < 5; )                            // cascaded folding  p = fold(p) 
            F( vec3( 1  , 0  , 0  ) ), 
            F( vec3(-.5 , .87, 0  ) ),
            F( C );   

        q.z--;  
        c = length(q + C*.72 * clamp(-dot(q,C*1.44) , 0., 1.) ) -.03;
        h = min( c,                                       // draw edge 
                 max( abs(q.z+.36)-.3 , .04-c) );         // icosahedron - hollow around edges
       P += .5* h*D;
    }
    O *= O*O * 1.2                                        // color scheme
      * ( c==h ? vec4(1,.9,.9,1) : vec4(.9,.9,1,1) );
}
