const vec2  rPhif2 = vec2(0.7548777, 0.5698403);
const vec3  rPhif3 = vec3(0.8191725, 0.6710436, 0.5497005);
const vec4  rPhif4 = vec4(0.8566749, 0.7338919, 0.6287067, 0.5385973);

