mat2 Rot(float a){
    float s = sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 20.;
    uv *= length(sin(uv)); // the line that makes this pattern interesting
    //uv *= Rot(length(uv*.3)-iTime); // make it a spiral
    vec3 col = vec3(0);
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);  //can put gv and id out of sync for interesting textures.
    float c = .2/length(gv)+.05/length(-gv);

    c = abs(c);
    float a = mix(0., 20., fract(iTime*.01)), b = mix(0., 20., fract(iTime*.03));
    col += c+sin(a*id.x+b*id.y)-length(uv+gv)*.1;
    col *= sin(vec3(.222, .666, .999)*iTime)*.5+.5;

    fragColor = vec4(col,1.0);
}
