//Buffer A is where it is at
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;

    //distance the ray travelled was saved in alpha channel
    float maxDist = texture(iChannel0,uv).a;

    vec2 texel = 1./iResolution.xy;
    vec4 total_color;
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }
    fragColor = total_color;

    //fwidth is really good at detecting sharp edges, so subtract them off
    //since they are causing flickering, subtract off more as we get further
    //away since the effect is worse with distance

    fragColor -= fwidth(fragColor) * exp(maxDist*maxDist/1000.);

}

#define extraReflection 1.  //set this to zero if your FPS is crashing - sorry
#define rot(a) mat2(cos(a+vec4(0,11,33,0)))

float num_iter, objid, inf=1e6, too_small=1e-6, 
    distToFloor, glow=0., maxDist=30., mmaxDist=30., tileColor=0.;
vec3 ifs_color, ro;

float de(vec3 p) {
    vec4 q = vec4(p, 3.);
    float nn=2.;
    q.xz = mod(q.xz + nn, 2.*nn) - nn;
    vec2 qq = floor(q.xz);
    q.xyz -= 1.;

    float i=0.;
    for(; i < params[1]; i++) {
        q.xyz = abs(q.xyz + (1.+params[32])*1.0) - (1.+params[32])*1.0*(1.+q.xyz/50.*(1.+params[19]));
        q /= clamp(dot(q.xyz, q.xyz), 0.4+params[2], 1.0+params[3]);
        q *= (1.+params[4]);
    }


    float d2 = length( max( abs(q.xyz) - vec3(1.2+params[5],1.2+params[6], 1.2+params[18]), 0.0) )/q.w;
    float d1 = p.y + .01*length(qq);  //tiles 
    float d0 = 2.4 - p.y;


    objid = 0.0;
    if ( d1 < d2 ) objid = 1.; 

    tileColor = d2;
    distToFloor = d1;

    return max( min(d0,min(d1 , d2)) , -(length(p-ro)-params[7]) );
}

float march(vec3 ro, vec3 rd, float mx) {
    float t = 0.0;

    float eps = 1e-5, distfac=40.;
    float hitThreshold = eps;
    for(int i = 0; i < 200; i++) {
        float d = de(ro + rd*t);
        if (d < hitThreshold || t >= mx) break;
        t += d;
        hitThreshold = eps*(1.+t*t*distfac);
        glow += .15*exp(-max(6.-d,0.)/10.);
    }
    return t;
}

vec3 phong(
  in vec3 pt,
  in vec3 prp,
  in vec3 normal,
  in vec3 light,
  in vec3 color,
  in float spec,
  in vec3 ambLight)
{
   vec3  lightv= (light-pt);
   float lightDist =  length(lightv);
   float diffuse=dot(normal,lightv);
   vec3  refl=-reflect(lightv,normal);
   vec3  viewv=normalize(prp-pt);
   float specular=pow(max(dot(refl,viewv),0.0),spec+params[20]);

   return  4.*exp(-lightDist*lightDist/10.)*( (max(diffuse,0.))*(color) + .2*specular*vec3(.9,.8,.6));  
}

vec3 normal(vec3 p, float dist) {
    vec2 h = vec2(0.0001 + params[25] + dist/2000., 0.0);
    vec3 n = vec3(
        de(p + h.xyy) - de(p - h.xyy),
        de(p + h.yxy) - de(p - h.yxy),
        de(p + h.yyx) - de(p - h.yyx));
    return normalize(n);
}


vec3 render(vec3 ro, vec3 rd) {


    vec3 col = vec3(0.);
    vec3 light_dir =  ro ; light_dir.z += mmaxDist - mod(iTime*8., mmaxDist*2.);
    vec3 color     = vec3( params[27], params[28], params[29]);
    float specular_power = 2.;
    vec3 ambient_light = vec3(.1);
    vec3 floor_color = vec3(0.3,0.2,.1);
    float t = march(ro, rd, mmaxDist);

    glow *= exp(-t*t*1e-2);
    maxDist = t;

    if(t < mmaxDist) {
        vec3 pos = ro + rd*t;
        vec3 nor = normal(pos, t);   
        vec3 cf = phong( pos, ro, nor, light_dir, color, specular_power, ambient_light);       
        col = cf;

        col += 1.2*exp(-t/50.)*objid*(floor_color + 25.*(tileColor*tileColor)*vec3(.5,.5,.2));

        if (params[21] > 0. ) {  //first reflection
            vec3 pos2 = ro + rd*.999*t; 
            vec3 ref = reflect(rd, nor);
            float tt =  march( pos2, ref, mmaxDist);
            glow *= exp(-tt*tt*3e-2);
            glow *= exp(-distToFloor/10.);

            if ( tt < mmaxDist) {
                vec3 ppos = pos2 + ref*tt;
                vec3 nnor = normal(ppos, tt);
                vec3 rref = reflect(ref, nnor);

                vec3 cf2 = phong( ppos, ro, nnor, light_dir, color, specular_power, ambient_light); 
                col += params[23] * cf2 *.5 ;                    


              if ( (params[21] + extraReflection) > 1.) {  //second reflection
                vec3 pos3 = pos2  + ref*.999*tt;
                float ttt = march( pos3, rref, mmaxDist);
                glow *= exp(-ttt*ttt*1e-4);

                if (ttt < mmaxDist ) {
                   vec3 pppos = pos3 + rref * ttt;
                   vec3 nnnor = normal(pppos, ttt);

                   vec3 cf3 = phong( pppos, ro, nnnor, light_dir, color, specular_power, ambient_light);
                   col += params[24] * cf3 *.5 ;

                }
              }
            }
        }

        col = clamp(col, 1e-6,inf);
        col *= col;
    }
    return col + 3e-10*pow(glow,8.)*vec3(.5,.3,.07) ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y*1.5;

    ro = vec3(1.,1.,iTime/5.);
    vec3 rd = normalize( vec3(p,2.) );


    if (iMouse.z > 0.) {
        vec2 an = 5.*(iMouse.xy/iResolution.xy-.5);
        vec2 cc=cos(an), ss=sin(an);
        rd.xz *= mat2(cc.x,-ss.x,ss.x,cc.x);
        rd.yz *= mat2(cc.y,-ss.y,ss.y,cc.y);
    }
    else {
        rd.xz *= rot(.6);
    }

    vec3 col = render(ro, rd);
    col = 1. - exp(-.3 * (1.+params[16]) * col);

    fragColor = vec4( col, maxDist);

}
