/*
This sketch has two phases:
1. User Interface mode, where you alter the fractal parameters via the arrows keys and the mouse.
   At any time you can capture the settings and save them in the source code as keyframes.
2. Keyframe display mode, where the settings are interpolated between the keyframes.

UserInterface mode:
Enable "#define USER_INTERFACE" at the top of the Common tab.
The interface is described just below.
Note the color bar drawn at screen bottom (when settings are displayed).
Follow the instructions in the "Console Transmit" sketch:   https://www.shadertoy.com/view/4XVGR3
to acquire the settings to raw text you can add to the keyframe array storage (Common tab, line #52).
A little tedious, but I don't know of any other way to get the settings out of the shader.

Keyframe display mode:
Comment out "#define USER_INTERFACE" at the top of the Common tab.
No user interface except: click the mouse on left/right 95% side of the screen to disable/enable cross eyed stereo.
The speed of keyframe transistion is set via the "iFramesPerKeyFrame" on line 416 of this tab.

Hope you find this technique useful in your experiments.

Jos leys' Kleinian:
https://www.shadertoy.com/view/lstyR4
https://www.shadertoy.com/view/Ns2fzy

*/

/* USER_INTERFACE -----------------------
Key Commands:
<Up,Dn Arrows> : select widget focus. PgUp,PgDn hop by 5 entries.
<Lt,Rt Arrows> : alter value of focused widget
<0>   : focused value -> 0.0
<1> ... <9>   load keyFrame dataset 0 ... 8
<Spc> : reset

Note: hold down <Shift> then drag mouse to affect camera.xy regardless of focus.
Note: hold down <Alt><Shift> then drag mouse to affect aim.xy regardless of focus.

Drag mouse on X axis to alter focused widget value.
Note: mouse X affects focused value, mouse Y affects next entry

Accelerated changes while holding down arrow keys or moving the mouse:
Hold down <A> for change Amount * 0.1
Hold down <Z> for change Amount * 10.
Hold down both <A><Z> for change Amount * 50.

Click the mouse on left/right side of the screen to disable/enable cross eyed stereo.

*/

// --------------------------------------------------------------------
// given 0..1, output ease in/out rendition 0..1
// https://stackoverflow.com/questions/13462001/ease-in-and-ease-out-animation-formula

float parametricBlend(float ratio) {
	float sqt = ratio * ratio;
	return sqt / (2.0 * (sqt - ratio) + 1.);
}

float bezierBlend(float ratio) {
	return ratio * ratio * (3. - 2. * ratio);
}

float parabolicBlend(float ratio) {
	float y = 2. * ratio * ratio;

	if (ratio > 0.5) {
		ratio -= 1.;
		y = -2. * ratio * ratio + 1.;
	}

	return y;
}

void loadKeyFrameEntry(int fieldIndex,int keyFrameIndex,float mixRatio) {
    int index1 = keyFrameIndex * keyframeEntryLength;
    int index2 = ((keyFrameIndex+1) % keyFrameCount) * keyframeEntryLength;

    float r = parametricBlend(mixRatio);

    int index = fieldIndex / 4;
    int field = fieldIndex % 4;
    q[index][field] = mix(keyFrame[index1+fieldIndex],keyFrame[index2+fieldIndex],r);
}

void loadKeyFrame(int keyFrameIndex,float mixRatio) {
    int index1 = keyFrameIndex * keyframeEntryLength;
    int index2 = ((keyFrameIndex+1) % keyFrameCount) * keyframeEntryLength;

    float r = parametricBlend(mixRatio);

    for(int i=0;i<QSIZE;++i)   // vec4 q[] index
        for(int j=0;j<4;++j)   // x,y,z,w field index
            q[i][j] = mix(keyFrame[index1++],keyFrame[index2++],r);
}

// --------------------------------------------------------

#define CRYSTAL_EFFECT

const float fogDistance = -1.5; // set to 0.0 to disable fog
const float fogAmount = 0.12;
const float fogColor = 0.32;

const float parallax = 0.03; // use smaller values when viewing closeup objects

float ambient,contrast,vibrant,dim;
float Ostrength; // 0.0 -> disable orbit trapping
float Ocycle;
float OXwt,OXcr; // weight, color code
float OYwt,OYcr;
float OZwt,OZcr;
float OWwt,OWcr;
vec3  Ocenter;   // origin offset
vec3 direction;
vec3 normal;
vec3 position;
vec3 color;
float depth;
int iter;
vec4 orbitTrap;
int focus,row,displayCount;
bool stereoEnable;
vec3 viewVector,sideVector,topVector;

// --------------------------------------------------------

float dot2(vec3 z) { return dot(z,z);}

vec3 wrap(vec3 x, vec3 a, vec3 s) {
    x -= s;
    return (x-a*floor(x/a)) + s;
}

vec2 wrap(vec2 x, vec2 a, vec2 s) {
    x -= s;
    return (x-a*floor(x/a)) + s;
}

void TransA(inout vec3 z, inout float DF, float a, float b) {
    float iR = 1. / dot2(z);
    z *= -iR;
    z.x = -b - z.x;
    z.y =  a + z.y;
    DF *= iR;
}

float DE(vec3 z) {
    float t = 0.;
    vec3 po = z;
    vec3 lz=z+vec3(1.), llz=z+vec3(-1.);
    float d=0.; 
    float d2=0.;
    float DE = 1e12;
    float DF = 1.;
    float ir,f = sign(qkleinI) * .45;    
    float kr = qkleinR * 0.6;
    float ki = qkleinI * 0.6;
    float ee = 14.0;

    z -= qInvCenter;
    d = length(z);
    d2 = d*d;
    z = (qrad*qrad/d2)*z + qInvCenter;


    for (int i = 0; i < 100; i++) {
        if(i >= int(qiterations)) break;

        ir = z.y * qkleinI / qkleinR; 
        z.x += ir;
        z.xz = wrap(z.xz, qboxSize * 2., -qboxSize);
        z.x -= ir;

        float kk = (z.x + ki) * 0.33;

        if (z.y >= kr + f * (2.*qkleinR-1.95)/4. * sign(kk) * (1. - exp(-(ee-(1.95-qkleinR)*(ee*2.2)) * abs(kk))))	
        { z = vec3(-qkleinI, qkleinR, qrotZ) - z; }

        TransA(z, DF, qkleinR, qkleinI);

        //If the iterated points enters a 2-cycle , bail out.
        if(dot(z-llz,z-llz) < 1e-5) break;

        //Store prÃ©vious iterates
        llz=lz; lz=z;

        if(i == 2) {
            po = z - orbit4;
            orbitTrap = min(orbitTrap, vec4(po, dot(po,po)));
        }
    }

    float y =  min(z.y, qkleinR - z.y);
    DE = min(DE, min(y, qdeMin) / max(DF, 2.0));
    return DE * d2 / (qrad + d * DE);
}

// --------------------------------------------------------

vec3 generateColor(float weight,float x) {
    if(weight == 0.0) return vec3(0.0);

    x = cos(x);
    //x=abs(sin(x)); // limits colors

    // bezier, you can rearange the functions for different color combos but this one is best
    float r = (1.0-x)*(1.0-x);
    float g = x*x;
    float b = 2.0*(1.0-x)*x;
    return vec3(r,g,b);
}

vec3 orbitTrapCycle(float cycle,vec3 c, float s) {
    float ss = s * cycle;
    return vec3(0.5) + 0.5 * vec3( cos(ss + c.x), cos(ss + c.y), cos(ss + c.z));
}

vec3 getOrbitColor() {
    vec3 orbitColor;

    if (Ocycle > 0.0) {
        orbitColor =
        orbitTrapCycle(Ocycle,generateColor(OXwt,OXcr), orbitTrap.x) * OXwt * orbitTrap.x +
        orbitTrapCycle(Ocycle,generateColor(OYwt,OYcr), orbitTrap.y) * OYwt * orbitTrap.y +
        orbitTrapCycle(Ocycle,generateColor(OZwt,OZcr), orbitTrap.z) * OZwt * orbitTrap.z +
        orbitTrapCycle(Ocycle,generateColor(OWwt,OWcr), orbitTrap.w) * OWwt * orbitTrap.w;
    } else {
        orbitColor =
            generateColor(OXwt,OXcr) * OXwt * orbitTrap.x +
            generateColor(OYwt,OYcr) * OYwt * orbitTrap.y +
            generateColor(OZwt,OZcr) * OZwt * orbitTrap.z +
            generateColor(OWwt,OWcr) * OWwt * orbitTrap.w;
    }

    return orbitColor;
}

// --------------------------------------------------------
// phongParam = diffuse,spec,ambient

void phongLight() {
    vec3 L = normalize(phongPosition - position);
    float dotLN = dot(L, normal);
    if (dotLN < 0.) return;

    float t1 = phongParam.x * dotLN;
    vec3 V = normalize(qcamera.xyz - position);
    vec3 R = normalize(reflect(-L, normal));
    float dotRV = dot(R, V);

    if (dotRV < 0.0) {
        color += phongColor * t1;
        return;
    }

    float t2 = phongParam.y * pow(abs(dotRV), phongParam.z);
    color += phongColor * (t1 + t2);
}

// spotLightParam = strength,x,y,exp
const float PI = acos(-1.);

void spotLight() {
    float a1 = spotLightParam.y * PI;
    float a2 = spotLightParam.z * PI * 0.5;
    float s1 = sin(a1);
    vec3 spotDir = normalize(vec3(s1 * cos(a2), s1 * sin(a2), cos(a1)));
    vec3 halfVector = normalize(spotDir - direction);

    float nDotL = max(0., dot(normal, spotDir));
    float hDotN = max(0., dot(normal, halfVector));

    vec3 c = spotLightColor;// * spotLightParam.x;
    float sexp = spotLightParam.w;

    color += c * ((sexp + 2.) / 2.) * pow(hDotN, sexp)
        * (sexp + (1. - sexp) * pow(1. - hDotN, 5.)) * nDotL * spotLightParam.x;
}

#define animate(v,v1,v2,speed) v = mix(v1,v2,0.5 + sin(iTime * speed) * 0.5)
#define animate2(v,base,delta,speed) v = mix(base-delta,base+delta,0.5 + sin(iTime * speed) * 0.5)
#define animate3(v,base,delta,speed) v = mix(base - base*delta,base + base*delta,0.5 + sin(iTime * speed) * 0.5)

void lighting() {
    animate2(phongParam.x,0.2,3.3,0.3);
    animate2(phongParam.y,0.,3.1,0.35);
    animate2(phongParam.z,8.,2.,0.4);
    animate2(phongColor.x,0.5,0.5,0.3);
    animate2(phongColor.y,0.5,0.5,0.7);
    animate2(phongColor.z,0.5,0.5,0.9);
    animate2(phongPosition.x,0.,2.,0.01);
    animate2(phongPosition.y,0.,2.,0.05);
    animate2(phongPosition.z,-5.,2.,0.07);

    animate2(spotLightParam.x,1.,1.5,0.3);
    animate2(spotLightParam.y,2.,1.5,0.03);
    animate2(spotLightParam.z,2.,1.5,0.035);
    animate2(spotLightParam.w,1.5,0.4,0.4);
    animate2(spotLightColor.x,0.5,0.5,0.1);
    animate2(spotLightColor.y,0.5,0.5,0.2);
    animate2(spotLightColor.z,0.5,0.5,0.4);

    animate(ostrength,0.5,0.4,0.1);
    animate(ocycle,0.,2.,0.18);
    animate(orbit2.x,-0.,3.,0.011);  // X wt,color
    animate(orbit2.y,-3.,3.,0.013);
    animate(orbit2.z,-0.,3.,0.015);  // Y wt,color
    animate(orbit2.w,-3.,3.,0.017);
    animate(orbit3.x,-0.,3.,0.019);  // Z wt,color
    animate(orbit3.y,-3.,3.,0.021);
    animate(orbit4.x,-3.,3.,0.023);  // center
    animate(orbit4.y,-3.,3.,0.025);
    animate(orbit4.z,-3.,3.,0.027);

    color += vec3(ambient) + vec3(1.0 - (normal * vibrant + sqrt(float(iter) * dim)));
    color = vec3(0.5) + (color - vec3(0.5)) * contrast;

    phongLight();
    spotLight();

    if(ostrength > 0.0) {
        vec3 oColor = getOrbitColor();
        color = mix(color, oColor, ostrength);
    }
}

// --------------------------------------------------------
const float MIN_DIST = 0.001;
const float MAX_DIST = 8.0;
const float EPSILON = 0.001; 

void rayMarch() {
    float distance;
    position = qcamera.xyz;
    iter = 0;
    depth = MIN_DIST;

    orbitTrap = vec4(10000.0);

    for(int i = 0;i < 255;++i) {
        distance = DE(position);
        if(abs(distance) < EPSILON || depth > MAX_DIST) break;
        iter += 1;

        depth += distance;
        position = qcamera.xyz + direction * depth;
    }
}

// --------------------------------------------------------
// https://iquilezles.org/articles/normalsSDF
void calcNormal() {
    float d0 = DE(position);
    const vec2 epsilon = vec2(.001,0);
    vec3 d1 = vec3(
        DE(position-epsilon.xyy),
        DE(position-epsilon.yxy),
        DE(position-epsilon.yyx));
    normal = normalize(d0 - d1);
}

// --------------------------------------------------------
// viewVectors already calculated in Buffer A

void setDirection(vec2 fragCoord) {
    // cross eyed stereo ------------------------------------
    vec2 srcP = fragCoord;        // copy of pixel coordinate; x is altered for stereo
    float xsize = iResolution.x;  // copy of window size; x is altered for stereo

    if(stereoEnable) {
        vec3 soffset = sideVector * parallax;
        xsize *= 0.5;             // window x size adjusted for 2 views side by side
        if(srcP.x >= xsize) {     // right side of stereo pair?
            srcP.x -= xsize;      // base 0  X coordinate
            qcamera.xyz -= soffset;// adjust for right side parallax
        }
        else {
            qcamera.xyz += soffset;// adjust for left side parallax
        }
    }

    vec2 vPos = (srcP - iResolution.xy * 0.5)/iResolution.y;
    direction = normalize(viewVector + vPos.x * sideVector + vPos.y * topVector);
}

#ifdef USER_INTERFACE // ==========================================

void loadDEParams() {
    for(int i=0;i<QSIZE;++i)
       q[i] = fetchData(iChannel0, storeAddr[i]);

    vec3 sd = fetchData(iChannel0, FOCUS_ADDR).xyz;
    focus = int(sd.x);
    displayCount = int(sd.y);

    sd = fetchData(iChannel0, UNIFORM_1).xyz;
    stereoEnable = bool(sd.x > 0.);

    viewVector = fetchData(iChannel0, VV_1).xyz;
    sideVector = fetchData(iChannel0, VV_2).xyz;
    topVector = fetchData(iChannel0, VV_3).xyz;
}

#else // ==========================================================

#define FUDGE_PLAYBACK   // alter keyframe datasets a small percentage via sine wave 
#define FUDGE_PLAYBACK2  // each data field travels keyframe dataset at it's own rate  (trying to avoid repeating images)

const int iFramesPerKeyFrame = 200;  // # image frames between keyframes (for me: 30 == 1 second)

void loadDEParams() {

#ifdef FUDGE_PLAYBACK2 
    for(int i=0;i<QSIZE * 4;++i) { 
        int frame = iFrame + int(float(iFrame) * float(i) * 0.125);
        int keyFrameIndex = (frame/iFramesPerKeyFrame) % keyFrameCount;
        float ratio = float(frame % iFramesPerKeyFrame) / float(iFramesPerKeyFrame);

        loadKeyFrameEntry(i,keyFrameIndex,ratio);
    }
#else   
    int keyFrameIndex = (iFrame/iFramesPerKeyFrame) % keyFrameCount;
    float ratio = float(iFrame % iFramesPerKeyFrame) / float(iFramesPerKeyFrame);
    loadKeyFrame(keyFrameIndex,ratio);
#endif

// fudge the data a little:  +-% from keyframe value
#ifdef FUDGE_PLAYBACK
    for(int i=0;i<QSIZE-2;++i) {  // -2 == not camera or aim
        float fi = float(i);
        q[i] += sin(iTime * (0.1 + fi * 0.2)) * q[i] * (0.01 + sin(float(iFrame) * 0.02) * 0.01);
    }
#endif    

    viewVector = normalize(q[AIM_INDEX].xyz);  // aim
    sideVector = normalize(cross(viewVector,vec3(0.,1.,0.)));
    topVector = cross(viewVector,sideVector);
}

#endif // USER_INTERFACE ============================================


#ifdef CRYSTAL_EFFECT

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 1.28318*(c*t) );
}

vec3 doPalette( in float val , in mat4 pType) {
  return palette( val ,  pType[0].xyz , pType[1].xyz , pType[2].xyz , pType[3].xyz );
}

#endif

// --------------------------------------------------------

void mainImage(out vec4 O, vec2 U) {
    loadDEParams();    

    color = vec3(0.);
    stereoEnable = iMouse.x > iResolution.x * 0.95;

    setDirection(U);
    rayMarch();

    if(depth < MAX_DIST) {
        calcNormal();

#ifdef CRYSTAL_EFFECT
        ambient = 0.0;
        contrast = 1.83;
        vibrant = 0.01;
        dim = 0.02;

        vec3 refr = refract(direction,normal, sin(iTime * 0.2));
        vec3 refl = normalize(reflect(vec3(0.,1.,0.),normal));

        // when using 3D cubemaps 
        //vec3 refrCol = texture(iChannel2,refr).xyz;
        //vec3 reflCol = texture(iChannel2,refl).xyz;

        // when using 2D textures
        float scaleTextureAccess = sin(iTime * 0.01) * 0.2;
        vec3 refrCol = texture(iChannel2,refr.xy * scaleTextureAccess).xyz;
        vec3 reflCol = texture(iChannel2,refl.xy * scaleTextureAccess).xyz;

        float reflectVal = pow( max( 0., dot(refl,direction)), 20.);

        const  mat4  paletteVal = mat4( .5 , .5 , .5 , 0.
                 , .5 , .5 , .5 , 0.
                 , 2. , 1. , 0. , 0.
                 , .5 , .2 , .25 , 0.);

        vec3 palCol = doPalette(sin(iTime * .002) * 10., paletteVal);
        vec3 refCol = doPalette( reflectVal , paletteVal ) * reflectVal;
        color = refCol + (palCol * refrCol);
#else
        ambient = 0.1;
        contrast = 1.4;
        vibrant = 0.1;
        dim = 0.01;
#endif

        lighting();
    }

    if(fogDistance != 0.0) {
        float f = depth - fogDistance;
        if(f > 0.0) {
            f = min(1.0,f * fogAmount);
            color = mix(color, vec3(0.2 + f * fogColor),f);
        }
    }

    O.xyz = color;

    if(stereoEnable && abs(U.x - iResolution.x * 0.5) < 1.5) O = vec4(1.0);

#ifdef USER_INTERFACE    
    if(displayCount > 0) {
        displayData(iChannel3,iResolution.xy,focus,O,U);
        console(O,U);
    }
#endif

}

//#define USER_INTERFACE

float ostrength = 0.3;
float ocycle = 2.;
vec4 orbit2 = vec4( 0.38347405, 1.86336986, -0.01788503, -2.71105793);
vec4 orbit3 = vec4(0.87534959, 1.04847687, 0.03234359, 0.00505466);
vec3 orbit4 = vec3(-2.19183836, 1.41098694, 1.56200985);
vec3 phongColor =  vec3(0.6,0.2,0.);
vec3 phongPosition = vec3(1.,-2.,6.);
vec3 phongParam = vec3(1.1,0.2,1.1);
vec4 spotLightParam = vec4(0.,2.1,-4.,0.09);
vec3 spotLightColor = vec3(0.783,0.422,0.0056);

#define qiterations q[0].x
#define qboxSize    q[0].yz
#define qkleinR     q[0].w
#define qkleinI     q[1].x
#define qrotZ       q[1].y
#define qdeMin      q[1].z
#define qrad        q[1].w
#define qInvCenter  q[2].xyz
#define qcamera     q[3].xyz
#define qaim        q[4].xyz
const int QSIZE =     5;
vec4 q[QSIZE];

#define AIM_INDEX 4

#define fetchData(buf, addr) texelFetch(buf, addr, 0)
#define storeDataAddr(buf_pos, addr) ivec2(buf_pos) == addr

// --------------------------------------------------------
const float iterate = 24.; // # iterations

const float keyFrame[] = float[](
iterate, -0.9702, 0.5260, 1.9605, 0.0179, 0.0000, 0.0560, 1.1470, -0.1800, 1.0320, 1.0220, 0., 0.4200, 1.6998, 2.3338, 0., -0.2055, -0.9540, -0.2180, 0.,
iterate, -0.9702, 0.5460, 1.9505, 0.0179, 0.0000, 0.0560, 1.1470, -0.0440, 0.9560, 1.0320, 0., 0.5200, 0.6097, 1.7238, 0., -0.2055, -0.9540, -0.2180, 0.,
iterate, -0.9702, 0.6570, 1.9535, 0.0230, 0.0000, 0.0610, 1.1470, -0.0440, 0.9560, 1.0520, 0., 0.6200, 0.6097, 1.7238, 0., -0.2055, -0.9540, -0.2180, 0., 
iterate, -0.7992, 0.6570, 1.9435, 0.0230, 0.0000, 0.0610, 1.1470, -0.2720, 1.0560, 1.0720, 0., 0.4200, 0.6097, 1.7238, 0., -0.2055, -0.9540, -0.2180, 0.,
iterate, -0.5812, 0.6670, 1.9405, 0.0280, 0.0000, 0.0610, 1.1470, -0.2920, 1.1560, 1.0920, 0., 0.5200, 0.6797, 2.1738, 0., -0.2063, -0.9293, -0.2286, 0.,
iterate, -0.4812, 0.6770, 1.9365, 0.0380, 0.0000, 0.0640, 1.1470, -0.3260, 1.2280, 1.1140, 0., 0.6800, 0.7197, 2.3538, 0.,  0.2270, -0.8737, -0.2232, 0.
);

const int keyframeEntryLength = QSIZE * 4;    // vec4 for each q[] entry
const int keyFrameCount = keyFrame.length() / keyframeEntryLength; // # q[] datasets in storage


// ========================================================================
#ifdef USER_INTERFACE
/*
const vec4  qq0 = vec4(32., -0.9702, 0.54603, 1.9605);
const vec4  qq1 = vec4(0.01789, 0.0, 0.05599, 1.147);
const vec4  qq2 = vec4(-0.18,1.032, 1.022,0.);
const vec4  qqcamera = vec4(0.62000000, 1.89975000, 2.3337,0.);
const vec4  qqaim    = vec4(-0.20554990, -0.95404933, -0.218034,0.);

const vec4  qq0 = vec4(24., -10.,0.326,1.67);
const vec4  qq1 = vec4(0.378,0., 0.05599, 1.147);
const vec4  qq2 = vec4(-0.18,1.319, 1.219,0.);
const vec4  qqcamera = vec4(-1.67,1.689,-1.43,0.);
const vec4  qqaim    = vec4(-1.976,0.8399,-2.8079,0.);
*/
const vec4  qq0 = vec4(32., -0.9702, 0.54603, 1.9605);
const vec4  qq1 = vec4(0.01789, 0.0, 0.056, 1.147);
const vec4  qq2 = vec4(-0.5999,1.6919,0.7120,0.);
const vec4  qqcamera = vec4(2.1199,0.7399,4.9599,0.);
const vec4  qqaim    = vec4(-0.9749,4.260,-3.5679,0.);

struct ParamData {
    float start,vmin,vmax,delta;
};

// leave room for future entries without having to adjust hardwired array positions
#define UNUSED  9.9
#define UNUSEDP ParamData(UNUSED,UNUSED,UNUSED,UNUSED)

const ParamData[] pData = ParamData[]( 
    //  0 p0 ---------------
    ParamData(qq0.x, 1.,40.,1.),
    ParamData(qq0.y, -10.,10.,0.01),
    ParamData(qq0.z, -10.,10.,0.01),
    ParamData(qq0.w, -10.,10.,0.01),
    //  4 p1 ---------------
    ParamData(qq1.x, -10.,10.,0.01),
    ParamData(qq1.y, -10.,10.,0.01),
    ParamData(qq1.z, -10.,10.,0.01),
    ParamData(qq1.w, -10.,10.,0.01),
    //  8 p2 ---------------
    ParamData(qq2.x, -10.,10.,0.01),
    ParamData(qq2.y, -10.,10.,0.01),
    ParamData(qq2.z, -10.,10.,0.01),
    UNUSEDP,
    //  12 p3 camera ---------------
    ParamData(qqcamera.x, -20., 20., 0.01), 
    ParamData(qqcamera.y, -20., 20., 0.01), 
    ParamData(qqcamera.z, -20., 20., 0.01), 
    UNUSEDP,
    // 16 p4 aim ---------------
    ParamData(qqaim.x, -20., 20., 0.01), 
    ParamData(qqaim.y, -20., 20., 0.01), 
    ParamData(qqaim.z, -20., 20., 0.01), 
    UNUSEDP
); 

const int PDATA_SIZE = 19;

const int indexCamera = 12;
const int indexAim = 16;

const ivec2 FOCUS_ADDR = ivec2(0, 0);
const ivec2 P0_ADDR =  ivec2(1, 0);
const ivec2 P1_ADDR =  ivec2(2, 0);
const ivec2 P2_ADDR =  ivec2(3, 0);
const ivec2 P3_ADDR =  ivec2(4, 0); // camera
const ivec2 P4_ADDR =  ivec2(5, 0); // aim
const ivec2 UNIFORM_1= ivec2(6, 0); // booleans
const ivec2 VV_1     = ivec2(7, 0); // view vectors
const ivec2 VV_2     = ivec2(8, 0);
const ivec2 VV_3     = ivec2(9, 0);

const ivec2[] storeAddr = ivec2[](P0_ADDR,P1_ADDR,P2_ADDR,P3_ADDR,P4_ADDR);

// =======================================================================
// font display technique I learned from cmgz's "The Chaos factory".  Thank you sir.
// https://www.shadertoy.com/view/XfdyRX

float drawChar(sampler2D chan,vec2 char_p, int char_id) { // https://www.shadertoy.com/view/llySRh
    if (char_p.x < .0 || char_p.x>1. || char_p.y<0. || char_p.y>1.) return 0.;
    vec2 p = char_p/16.; 
    return textureGrad(chan, p + fract(vec2(char_id,15-char_id/16)/16.), dFdx(p), dFdy(p)).x;
}

void printChar(sampler2D chan,inout vec2 position,int ascii,inout float result) {
    result = max(result, drawChar(chan,position,ascii));
    position.x -= 0.5;
}

#define printString(chan,position,asciiArray,result) for(int i=0;i< asciiArray.length();i++) printChar(chan,position,asciiArray[i],result);

void printInt(sampler2D chan,inout vec2 position,int value, inout float result) {
    int[8] digits;
    int count = 0;
    if(value < 0) { printChar(chan,position,45,result);  value = -value; }

    for(;;) {
        digits[count] = value % 10;        
        if(++count >= 8) break;

        value /= 10;        
        if(value == 0) break;
    }

    for(int i = count-1; i >= 0; i--)
        printChar(chan,position,48+digits[i],result);
}

void printFloat(sampler2D chan,inout vec2 position,float value, inout float result) {
    if(value < 0.) { printChar(chan,position,45,result); value = -value; }        
    int i = int(value);
    printInt(chan,position,i,result);
    printChar(chan,position,46,result);
    i = int(fract(value) * 10000.);
    printInt(chan,position,i,result);
}

