// A65,B66,C67,D68, E69, F70, G71, H72, I73, J74, K75, L76, M77, N78, O79, P80, Q81, R82, S83, T84, U85, V86, W87, X88, Y89, Z90
// a97,b98,c99,d100,e101,f102,g103,h104,i105,j106,k107,l108,m109,n110,o111,p112,q113,r114,s115,t116,u117,v118,w119,x120,y121,z122

const int titer[] = int[](73,116,101,114,97,116,105,111,110,115);
const int tboxx[] = int[](66,111,120,32,88);
const int tboxy[] = int[](66,111,120,32,89);
const int tkleinR[] = int[](75,108,101,105,110,32,82);
const int tkleinI[] = int[](75,108,101,105,110,32,73);
const int trotz[] = int[](82,111,116,32,90);
const int tdemin[] = int[](68,69,109,105,110);
const int tradius[] = int[](82,97,100,105,117,115);
const int tinvcenterx[] = int[](73,110,118,67,101,110,116,101,114,88);
const int tinvcentery[] = int[](73,110,118,67,101,110,116,101,114,89);
const int tinvcenterz[] = int[](73,110,118,67,101,110,116,101,114,90);
const int tCameraX[] = int[](67,97,109,101,114,97,88);
const int tCameraY[] = int[](67,97,109,101,114,97,89);
const int tCameraZ[] = int[](67,97,109,101,114,97,90);
const int tAimX[] = int[](65,105,109,88);
const int tAimY[] = int[](65,105,109,89);
const int tAimZ[] = int[](65,105,109,90);

const float textSize = 9.; // smaller value -> larger text   
const vec2 textUL = vec2(1.78,-0.88);

#define Entry(legend,value) \
    printString(channel,p2,legend,char_d); \
    printChar(channel,p2,32,char_d); \
    printFloat(channel,p2,value,char_d); \
    entry = true;

void displayData(sampler2D channel, vec2 R, int focusIndex, inout vec4 O, vec2 U) {
    vec2 uv = (2.0 * U - R.xy)/R.y;   // screen resolution
    vec2 p2,p = textSize * (textUL + uv);
    vec3 col = vec3(0.);
    bool entry;
    float char_d = 0.;

    for(int i=0;i< PDATA_SIZE;++i) {
        entry = false;
        p2 = p;

        switch(i) {
            case  0 : Entry(titer,q[0].x); break;
            case  1 : Entry(tboxx,q[0].y); break;
            case  2 : Entry(tboxy,q[0].z); break;
            case  3 : Entry(tkleinR,q[0].w); break;
            case  4 : Entry(tkleinI,q[1].x); break;
            case  5 : Entry(trotz,q[1].y); break;
            case  6 : Entry(tdemin,q[1].z); break;
            case  7 : Entry(tradius,q[1].w); break;
            case  8 : Entry(tinvcenterx,q[2].x); break;
            case  9 : Entry(tinvcentery,q[2].y); break;
            case 10 : Entry(tinvcenterz,q[2].z); break;
            case 12 : Entry(tCameraX,qcamera.x); break;
            case 13 : Entry(tCameraY,qcamera.y); break;
            case 14 : Entry(tCameraZ,qcamera.z); break;
            case 16 : Entry(tAimX,qaim.x); break;
            case 17 : Entry(tAimY,qaim.y); break;
            case 18 : Entry(tAimZ,qaim.z); break;
        }

        if(entry) {
            if(char_d > 0.5) {
                col = (i == focusIndex) ? vec3(1.,1.,0.) : vec3(0.8);    
                O = vec4(col,1.0);
                return;
            }

            p.y += 0.84;
        }
    }
}

// COLOR CODED CONSOLE =============================================================
#define _MINUS   10
#define _DECIMAL 11
#define _COMMA   12
#define _CR      13
#define _SPC     14
#define _END     15
#define DP_MAXLENGTH 500

struct Chars {
    int[DP_MAXLENGTH+1] data;
    int index;
}; 

bool printFloat2(inout Chars c,float val,int fCount) {
    if(c.index >= DP_MAXLENGTH - 15) return false; // nearing buffer overflow

    // truncate digits
    int t = int(val * 1000.0);
    val = float(t) * 0.001;    

    if(val < 0.) {
        c.data[c.index++] =_MINUS;
        val = -val;
    }

    int[8] digits;
    int count = 0;

    int iVal = int(val);
    val -= float(iVal);

    for(;;) {
        digits[count++] = iVal % 10;
        if(count >= 8) break;
        iVal /= 10;
        if(iVal == 0) break;
    }    
    for(int i = count-1; i >= 0; i--)
        c.data[c.index++] = digits[i];

    if(fCount == 0) return true;

    c.data[c.index++] = _DECIMAL;

    for(int i = 0; i < fCount;++i) {
        val *= 10.0;
        int digit = int(val);
        c.data[c.index++] = digit;
        val -= float(digit);
        if(val <= 0.0) break;
    }

    return true; 
}

bool printFloat(inout Chars c,float val) { return printFloat2(c,val,2); }
bool printInt(  inout Chars c,int val)   { return printFloat2(c,float(val),0); }

void console(inout vec4 fragColor, vec2 fragCoord) {

    if(fragCoord.y >= 10.) return;

    Chars chr;
    chr.index = 0;

    // encode your data in the buffer ----------
    for(int i=0;i<QSIZE;++i) {
        for(int j=0;j<4;++j) {
            if(!printFloat(chr,q[i][j])) break;
            chr.data[chr.index++] = _COMMA;
            chr.data[chr.index++] = _SPC;
        }
    }

    for(int i=0;i<3;++i) 
        chr.data[chr.index++] = _END;  // signal console to stop parsing the pixels

    // ------------------------------------------
    // all data is encoded in the buffer.
    // now grab the value assigned to the current pixel 
    fragColor = vec4(0.,0.,0.,1.);

    int index = int(fragCoord.x) / 3;       // each character cell is drawn 3 pixel wide

    if(index < chr.index) {                
        int v = chr.data[index];            // 0 ... 15
        fragColor.z = float(v & 3) * 0.25;  // low 2 bits
        v >>= 2;
        fragColor.y = float(v & 3) * 0.25;  // high 2 bits        
    }
}

