#ifdef USER_INTERFACE

void initializeStorage(inout vec4 O,vec2 U) {
    if (storeDataAddr(U, FOCUS_ADDR)) O.xyz = vec3(0.);

    int pIndex = 0;
    for(int i=0;i<QSIZE;++i) {
        if(storeDataAddr(U, storeAddr[i])) {
            O = vec4(0.);
            if(pIndex+0 < PDATA_SIZE) O.x = pData[pIndex+0].start;
            if(pIndex+1 < PDATA_SIZE) O.y = pData[pIndex+1].start;
            if(pIndex+2 < PDATA_SIZE) O.z = pData[pIndex+2].start;
            if(pIndex+3 < PDATA_SIZE) O.w = pData[pIndex+3].start;
        }

        pIndex += 4;
    }
}

// --------------------------------------------------------
// determine these values as seldom as possible to save work for image shader

void updateViewVectors(inout vec4 fragColor,vec2 fragCoord) {
    if (storeDataAddr(fragCoord, VV_1) || storeDataAddr(fragCoord, VV_2) || storeDataAddr(fragCoord, VV_3)) {
        vec3 viewVector = normalize(fetchData(iChannel0, P4_ADDR).xyz);  // aim
        vec3 sideVector = normalize(cross(vec3(0.,1.,0.),viewVector));
        vec3 topVector = cross(viewVector,sideVector);

        if(storeDataAddr(fragCoord, VV_1)) fragColor = vec4(viewVector,1.);
        if(storeDataAddr(fragCoord, VV_2)) fragColor = vec4(sideVector,1.);
        if(storeDataAddr(fragCoord, VV_3)) fragColor = vec4(topVector,1.);
    }
}

// --------------------------------------------------------
// keyboard: https://www.shadertoy.com/view/lsXGzf

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
const int KEY_SHIFT  = 16;
const int KEY_CTRL  = 17;
const int KEY_ALT  = 18;
const int KEY_PGUP  = 33;
const int KEY_PGDN  = 34;

bool keyClick(int ascii) {
	return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}

bool getKeyState(int keyCode){
    return bool(texelFetch(iChannel1, ivec2(keyCode,0), 0).x != 0.);
}
bool getKeyClick(int keyCode){
    return bool(texelFetch(iChannel1, ivec2(keyCode,1), 0).x != 0.);
}

void acceleratedValue(inout float value) {
    bool aKey = getKeyState(65);
    bool zKey = getKeyState(90);

    if(aKey && zKey) value *= 50.; else
    if(aKey) value *= 0.1; else
    if(zKey) value *= 10.0;    
}

// --------------------------------------------------------

bool alterFocusedVariable(int focus,int axis) {
    if(focus > PDATA_SIZE-1) return false;
    float changeAmount = 0.;
    int focusIndex = focus / 4;  // memory index
    int focusField = focus % 4;  // x,y,z,w field within

    if(getKeyClick(48)) {  // '0' sets value to 0.
        q[focusIndex][focusField] = 0.;
        changeAmount = 1.;   // so function returns true
    }
    else {
        if(getKeyState(69)) { // 'E'
            changeAmount = sin(iTime) * 0.001;
        }
        else {
            if(axis == 0) { // arrow keys affect only focused variable 
                if(getKeyState(KEY_LEFT))  changeAmount = -pData[focus].delta; else
                if(getKeyState(KEY_RIGHT)) changeAmount = +pData[focus].delta;
            }

            // mouse affects focused variable and also next one --------------
            if(iMouse.z > 0.0) { // left mouse button down
                vec2 delta = (iMouse.xy - abs(iMouse.zw)) * 0.01 * pData[focus].delta;
                if(axis == 0) changeAmount = delta.x;
                if(axis == 1) changeAmount = -delta.y; // flip Y axis
             }
        }

        if(changeAmount != 0.) {
            acceleratedValue(changeAmount);
            q[focusIndex][focusField] += changeAmount;
        }
    }

    q[focusIndex][focusField] = clamp(q[focusIndex][focusField],pData[focus].vmin,pData[focus].vmax);

    return changeAmount != 0.;
}

// --------------------------------------------------------

void loadKeyFrameDataset(int index, inout vec4 O, vec2 U) {
    int base = index * QSIZE * 4;
    for(int i=0;i<QSIZE;++i) {
        for(int j=0;j<4;++j) 
            q[i][j] = keyFrame[base++];
        if (storeDataAddr(U, storeAddr[i])) 
            O = q[i];
    }       
}

// --------------------------------------------------------

void mainImage(out vec4 O, vec2 U) {
    O = vec4(0.); 
    if(U.x > 30.0 || U.y > 1.0) return; // skip unused storage locations

    if(iFrame < 2 || keyClick(32)) {  // reset settings on coldstart or <Spc> keypress
        initializeStorage(O,U);
        updateViewVectors(O,U);
        return; 
    }

    updateViewVectors(O,U);

    // ==========================================================       
    // direct load keyframe datasets via '1'...'9'
    for(int i=49;i<=57;++i)
        if(getKeyClick(i)) {
            loadKeyFrameDataset(i-49, O,U);
            return;
        }

    // ==========================================================       
    // keyboard & mouse alter focused data (mouse also affects next entry)

    // fetch current values
    for(int i=0;i<QSIZE;++i) {
        q[i] = fetchData(iChannel0, storeAddr[i]);

      // animate
      //if(i < QSIZE - 2) // not camera,aim
      //    q[i] *= (1. + sin(iTime * float(i) * 0.03) * (0.00003 + float(i) * 0.00001));
    }

    bool resetDisplayCount = false; 
    int focus = int(fetchData(iChannel0, FOCUS_ADDR).x);

    //-------------
    // if <Alt><Shift> alter target position.xy regardless of focus    
    if(getKeyState(KEY_SHIFT) && getKeyState(KEY_ALT)) 
        focus = indexAim;
    else

    // if <Shift> alter camera position.xy regardless of focus
    if(getKeyState(KEY_SHIFT)) 
        focus = indexCamera;
    //-------------

    if(alterFocusedVariable(focus,  0)) resetDisplayCount = true;
    if(alterFocusedVariable(focus+1,1)) resetDisplayCount = true;

    // output possibly altered values0-----------
    for(int i=0;i<QSIZE;++i)
        if (storeDataAddr(U, storeAddr[i])) 
            O = q[i];
    // ==========================================================       

    // alter focus.  update displayCount --------
    if (storeDataAddr(U, FOCUS_ADDR)) {
        O = fetchData(iChannel0, FOCUS_ADDR);
        float focusChange = 0.;
        if(getKeyClick(KEY_UP))   focusChange = -1.;
        if(getKeyClick(KEY_DOWN)) focusChange = +1.;
        if(getKeyClick(KEY_PGUP)) focusChange = -5.;
        if(getKeyClick(KEY_PGDN)) focusChange = +5.;

        if(focusChange == 0. && (iFrame % 12 == 0)) { // slow down auto repeat of up/dn arrows
            if(getKeyState(KEY_UP))   focusChange = -1.;
            if(getKeyState(KEY_DOWN)) focusChange = +1.;
        }

        if(focusChange != 0.) {

            //skip past UNUSED rows
            while(true) {
                O.x += focusChange;
                if(O.x < 0.0) O.x = float(PDATA_SIZE-1); else
                if(O.x >= float(PDATA_SIZE)) O.x = 0.0;

                if(pData[int(O.x)].start != UNUSED) break;
            }

            resetDisplayCount = true; 
        }

        if(resetDisplayCount) O.y = iFrameRate * 2.; else
        if(--O.y < 0.0) O.y = 0.0;  // so widget display times out 
    }   

    // ---------------------------------------------------
    if (storeDataAddr(U, UNIFORM_1)) {
        O = fetchData(iChannel0, UNIFORM_1); 
        if(iFrame < 10) 
            O.xyz = vec3(0.);
        else {
            if(getKeyClick(83)) O.x = (O.x == 0.) ? 1. : 0.; // S
        }
    }


}

