// x is top layer
vec3 blend(vec4 x, vec4 y)
{
    return (x.rgb * x.a) + (y.rgb * (1.0f - x.a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = fragCoord / iResolution.xy + iTime * 0.1f;

    vec2 uv2 = fragCoord / iResolution.xy;
    uv2.x -= iTime * 0.09f;
    uv2 *= 1.1f;

    float p = (texture(iChannel0, uv1).r + texture(iChannel0, uv2).r) * 1.0f;

    vec3 color;



    if (p > 0.4f && p < 1.5f)
    {
        color = vec3(0.0f, 0.7f, 1.0f);
    }
    else if (p <= 0.4f)
    {
        color = blend(vec4(p, p, p, 0.2f), vec4(0.0f, 0.7f, 1.f, 1.0f));
    }
    else
    {
        color = vec3(1.0f);
    }

    fragColor = vec4(color, 1.0f);
}
