Main {

    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));


    Q.w -= .5*(n.x-s.x-e.y+w.y);
    Q.xy *= 1.-.1*B(U).x*length(Q.xy)*abs(Q.w);

    if (U.y < .7*R.y
        &&U.y>.3*R.y&&U.x<3.) 
    for (float i = 0.; i < 5.; i++) 
        Q.w += 2.*cos(U.x+3.14159*i/5.*float(iFrame));

     if (U.x < .3*R.x&&U.y>.25*R.y&&U.y<.75*R.y&&abs(U.y-.5*R.y)>.05*R.y)
         Q *= 0.;



}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
float ln (vec2 p, vec2 a, vec2 b) { 
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

Main {
    if (iFrame >1) discard;
    U = 6.*(U-.5*R)/R.y;
    U -= vec2(2.5,-.5);
    U = U.yx;
    U.y = abs(U.y);
    Q = vec4(0);
    float a = -.5;
    U.xy *= mat2(cos(a),sin(a),-sin(a),cos(a));
    float l = length(U-vec2(-.1,.5));
    Q += .5*smoothstep(.05,0.03,abs(l-1.4));
    Q += .2*smoothstep(.1,.05,l-1.4);
    for (float i = 3.; i < 20.; i++) {

        Q += smoothstep(.05*i,.05,ln(U,vec2(0,0),vec2(0,.5)))/(i);

        U.x = abs(U.x);
        U.y -= .5;

        float a = -.5-.02*i;

        U.xy *= (1.5-.02*i)*mat2(cos(a),sin(a),-sin(a),cos(a));



    }



}

Main {
    Q = B(U)*.999;
    vec4 a = A(U);

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));

    a = .25*(n+e+s+w);
    Q += .01*length(a.xy*a.w);

}
