float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 finalColor = vec3(1, 1, 1);


    vec2 uv = fragCoord / iResolution.xy;

    float dl = length(uv);
    float d = fract(dl);
    d = d * d;

    float offset = sin(iTime);

    if (dl >= 1.0) {

        vec2 uvR = uv + vec2(rand(vec2(1, 3)), rand(vec2(4, 2)));
        vec2 uvB = uv + vec2(rand(vec2(4, 7)), rand(vec2(8, 4))); 

        finalColor = vec3(rand(uvR), 0, rand(uvB));
        if (finalColor.x >= 0.6) {
            finalColor = vec3(0,0,0);
        } else if (finalColor.z >= 0.95) {
            finalColor = vec3(offset, offset, offset);
        } else if (finalColor.z >= 0.93) {
            finalColor = vec3(1,1,1);
        } else if (finalColor.z >= 0.6) {
            finalColor = vec3(0,0,0);
        }
        vec2 uvoff = (fragCoord + vec2(rand(vec2(iTime, iTime)), rand(vec2(iTime, iTime)))) / iResolution.xy;
        float b = length(uvoff);
        b = fract(-b);
        b = b*b*b*b*b*b*b*b;
        finalColor += vec3(0.0, b, b);


    } else {
        finalColor = vec3(0.0, (d/2.0), d);

        if (d >= 0.0) {
            float r = rand(uv);
            if (r >= 1.0) {
                //code for stuff on the surface of the planet
            }
        }

    }

    float r = 0.0;

    vec3 newColor = vec3(r,0,0);


    finalColor += newColor;

    fragColor = vec4(finalColor,1.0);
}
