void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 ouv = uv;

    uv = fract(uv * 2.0) - 0.5;

    float d = length(uv);

    vec3 color = pallete(length(ouv) + iTime);

    d = abs(sin(d * 8. + iTime))/8.;
    d = 0.02 / d;

    fragColor = vec4(color * d, 1.0);
}

vec3 pallete(float t) {
    vec3 a = vec3(.5, .5, .5);
    vec3 b = vec3(a);
    vec3 c = vec3(1., 1., 1.);
    vec3 d = vec3(.263, .416, .557);

    return a+b*cos(6.28318*(c*t+d));
}
