#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e;vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e*e;
    p.z-=4.;
    p.xz*=R2D(iTime*.3);

    for(j=0.;j++<4.;)
        p.xz=abs(abs(p.xz*R2D(i))),
        d=min(atan(p.z,p.y),exp2(p.x))/9.,
        d-=dot(p,p)/2e4;

    e-=d;
}
o+=vec4(sqrt(p*j),1)*3./i;

}
