#define pi acos(-1.0)  

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    p = p*5.;
    p = fract(p);
    p -= 0.5;

    vec3 col = vec3(0.0);
    float a = atan(p.x, p.y);
    col += smoothstep(0.0, 2.0, min(0.01/abs(length(p) - 0.2), 0.8));
    col *= (sin(a + iTime*pi/2.)*sin(a + iTime*pi+vec3(0.6, 0.1, 0.0))*2.0+1.);
    p = clamp(p, -2.5,2.5); 
    col += step(0.0,sdBox(p,vec2(0.45)))*((sin(a + iTime*pi/2.)*sin(a + iTime*pi+vec3(0.6, 0.1, 0.0))*0.5+0.5))*step(0.0,-sdBox(p,vec2(2.52)));

    fragColor = vec4(col,1.0);
}
