void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 画面解像度の取得
    vec2 uv = fragCoord / iResolution.xy;

    // 画面のアスペクト比の調整
    uv.x *= iResolution.x / iResolution.y;

    // LEDのサイズ（小さく調整）
    float ledSize = 0.005;

    // マウスの位置を取得（正規化された座標）
    vec2 mousePos = iMouse.xy / iResolution.xy;
    mousePos.x *= iResolution.x / iResolution.y;

    // LEDのグリッド位置
    vec2 ledPosition = floor(uv / ledSize) * ledSize;

    // LEDの中央を計算
    vec2 ledCenter = ledPosition + ledSize * 0.5;

    // マウスとLEDの距離を計算
    float distToMouse = length(ledCenter - mousePos);

    // 距離に基づく輝度を計算
    float brightness = smoothstep(0.1, 0.0, distToMouse);

    // グロー効果の範囲と強度を計算
    float glow = smoothstep(0.15, 0.05, distToMouse) * 0.5;

    // LEDのカラーを設定（色は固定、輝度は距離に基づく）
    vec3 color = vec3(1.0, 0.8, 0.0); // 暖かい黄色のLED

    // フラグメントカラーの設定（グロー効果を追加）
    fragColor = vec4(color * (brightness + glow), 1.0);
}
