const float halfpi = asin(1.);

vec3 plane2sphere(vec2 p) {
    float r = length(p);
    float c = 2.*r / (dot(p, p)+1.);
    return vec3(vec2(p*c/r), sin(2.*atan(r)-halfpi));
}

vec3 rotate(vec3 v, float xz, float yz) {
    float c1 = cos(yz);
    float s1 = sin(yz);
    float c2 = cos(xz);
    float s2 = sin(xz);
    vec3 u = vec3(v.x, c1*v.y-s1*v.z, c1*v.z+s1*v.y);
    return vec3(c2*u.x-s2*u.z, u.y, c2*u.z+s2*u.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mouse = iMouse.xy == vec2(0) ? vec2(0.5*iTime, 0.) : (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec3 v = rotate(plane2sphere(5.*uv), -2.*mouse.x, 2.*mouse.y);
    vec2 p = vec2(atan(v.y, v.x), asin(v.z))/halfpi;
    vec2 a = mod(p, 0.8);
    vec3 col = (max(a.x, a.y) < 0.4 || min(a.x, a.y) > 0.4) ? vec3(0.6) : vec3(0.9);
    if (p.y < 0.) col *= vec3(0, 0.7, 0.8);

    fragColor = vec4(col, 1.);
}
