vec4 gradient(in vec2 fragCoord, in float lightness) {
    vec2 uv = fragCoord / iResolution.xy;

    float gradient = uv.x/.9;

    vec3 colorStop1 = vec3(1.0, 1.0, 1.0); // white
    vec3 colorStop2 = vec3(0.0, 0.0, 0.0); // black

    vec3 gradientColor = mix(colorStop1*lightness, colorStop2*lightness, gradient);

    return vec4(gradientColor, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float borderThickness = 0.01;

    if (uv.y < borderThickness || uv.y > 1.0 - borderThickness) {
        fragColor = gradient(fragCoord, 1.0);
    } else {
        fragColor = gradient(fragCoord, 0.6);
    }
}
