void mainImage(out vec4 fragColor,
               in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Compute displacement in texture
    vec3 col = texture(iChannel0,
                       (uv-0.5)
                       * pow(length(uv-0.5),
                             (sin(iTime)+1.0))
                       + 0.5).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
