float rect(vec2 p, vec2 size) { 
  vec2 d = abs(p) - size;
  return min(max(d.x, d.y), 0.0) + length(max(d,0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= 10.0;
    uv = fract(uv);

    //rect
    float abc = rect(uv - vec2(0.5, 0.0), vec2(0.49, 0.5));
    float abc2 = rect(uv - vec2(0.5, 0.25), vec2(0.35, 0.6));
    float d = 1.0 - smoothstep(0.0, 1.0 / 2000.0, abc);
    float d2 = 1.0 - smoothstep(0.0, 1.0 / 400.0, abc2);

    d = 1.0 - d2 * d;


    vec3 col = vec3(d, d, d);
    // Output to screen
    fragColor = vec4(col,1.0);
}
