#define ITER 128.

mat2 Rot(float a){
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float Mandelbrot(vec2 uv){
    vec2 c = 5.*uv - vec2(.7, 0.);
    vec2 z = vec2(0.);
    float iter = 0.;
    for(float i = 0.;i<ITER;i++){
        z = vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y) + c;
        if(dot(z,z) >5.) return iter/ITER;
        iter++;
    }
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= .3;
    uv.x = -abs(uv.x); //makes it look like funky glasses.
    vec3 col = vec3(0);

    float m = Mandelbrot(uv);

    col += m*(sin(vec3(1.245, 2.356+uv.y, 3.689+uv.x)+iTime)*.5+.6);
    col = pow(col, vec3(.45));

    fragColor = vec4(col,1.0);
}
