#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0);

    // L-system-like parameters
    float time = iTime * 0.2;
    float layers = 3.0;

    for(float i = 0.0; i < layers; i++) {
        // Create rotating layer
        float angle = time + i * 2.0 * PI / layers;
        vec2 rotUV = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * uv;

        // Generate crescent shape
        float crescent = smoothstep(0.3, 0.302, length(rotUV)) - 
                        smoothstep(0.4, 0.402, length(rotUV));

        // Add sharp details
        float detail = sin(atan(rotUV.y, rotUV.x) * 2.0 + time * 3.0) * 0.5 + 0.5;
        crescent *= detail;

        // Layer colors
        vec3 layerCol = 0.5 + 0.5 * cos(vec3(0.882,0.000,1.000) + i * 4.0 + time);
        col += crescent * layerCol;
    }

    // Add glow effect
    col = pow(col, vec3(0.9));

    fragColor = vec4(col, 1.0);
}
