void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy; 
     uv -= 0.5;
     uv.x *= iResolution.x / iResolution.y; 
     float t = iTime;


    vec2 uv1 = uv; 
    uv1.x += sin(iTime);


    float upper = 100.0; 
    float lower = 40.0; 
    float midline = (upper + lower) / 2.0;
    float freq = (upper - midline) * sin(iTime*3.0) + midline; 


    float wave1 = sin(freq * length(uv1) - t); // Remove amplitude and time shift

    // Map wave (-1 to 1) to color intensity (0 to 1)
    wave1 = wave1 * 0.5 + 0.5;

    vec2 uv2 = uv; 
    uv2.x -= sin(iTime);

    float wave2 = sin(freq * length(uv2) - t); 
    wave2 = wave2 * 0.5 + 0.5;

    vec2 uv3 = uv; 
    uv3.y += 0.2;

    float wave3 = cos(freq * length(uv3) - t); 
    wave3 = wave3 * 0.5 + 0.5;

    float wave = wave1 *  wave2 * wave3;

    float intensity = smoothstep(1.0, 0.0, length(uv));

    float bluesmooth = smoothstep(0.0, 0.3, length(uv));
    float redsmooth = smoothstep(0.0, 0.3, length(uv)-0.3);
    float greensmooth = smoothstep(0.0, 0.3, length(uv)-0.6);



    vec4 color1 = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 color2 = vec4(0.1 + redsmooth  + cos(iTime), 0.4 + greensmooth,0.4 + bluesmooth , 1.0); 

    vec4 color = mix(color1, color2, wave);

    // Use the wave value to set the red channel intensity
    fragColor = (color + (color * intensity)); // Visualize using grayscale
    // fragColor = color;
}
