void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 correctedUV = (uv - vec2(0.5)) * aspectRatio + vec2(0.5);

    vec4 lastTex = texture(iChannel2, uv);
    vec3 col = lastTex.rgb;

    // Check multiple circles using mathematical expressions
    for (int i = 0; i < 10; i++) {
        float angleOffset = float(i) * 1.57*0.9; // 90deg * 0.9
        float radius = 0.05;//0.35 - float(i) * 0.1;
        vec2 center = vec2(0.5) + vec2(sin(iTime + angleOffset), cos(iTime + angleOffset)) / (3.0 + float(i));
        float dist = distance(correctedUV, center);
        if (dist < radius) {
            col.rgb = texture(iChannel0, uv).rbr / 2.0;
        }
    }

    fragColor = vec4(col, 1.0);
}
