// Rotation matrix
mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    //lots and lots of trial and error
    uv *= 10.;
    float spiral = 3.-sin(iTime-length(uv));
    uv *= Rot(3.1415*spiral)/4.;
    uv = fract(uv);
    uv -=.5;

    //float d = smoothstep(.3, .25, length(uv)+.1); // not bright enough. 
    float d = .2/length(uv);

    float fade = 0.;//abs(sin(iTime));
    col += d*fade;
    col.rgb += uv.xxx*spiral;

    fragColor = vec4(col,1.0);
}
