// variation of 
// https://www.shadertoy.com/view/4csBzX
#define t iTime

void mainImage(out vec4 o, vec2 u) {
    vec2   r = iResolution.xy, q,
           p = vec2(u + u - r) / r.y,

           polar0 = vec2(
                        atan(p.y, p.x) ,
                        length(p) / dot(p, p) - 1.
                    ) * 12. / 3.14,

           polar1 = vec2(
                        atan(p.y, p.x) ,
                        log(length(p))
                    ) * 12. / 3.14,

           polar2 = p * 18. / dot(p, p);


    float T = mod(t * .12, 5.),
          a = smoothstep(1., 1.2, T), 
          b = smoothstep(2., 2.3, T), 
          c = smoothstep(3., 3.3, T), 
          d = smoothstep(4., 4.2, T); 

    p = mix(8. * p, 
            mix(polar1.yx, 
                mix(polar2, 
                    mix(polar1, 
                        polar0, 
        d), c), b), a);


    // normal rain code. 
    // see: https://www.shadertoy.com/view/4fByDV
    float  h = 34.,
           k = floor(p.x),
           t = h * fract(sin(k * 453.2734) * 994.3434) + t * .75;

    o *= 0.;
    p.y += sin(k + t) + fract(t);
    p.y < 0. ? h = 1. : h;
    u = fract(p) - .5;
    p.y += (h - 1.) / 2. * fract(t);
    q = vec2(u.x, mod(p.y, h) - h / 2.);
    o = vec4(
            smoothstep(k = 24. / r.y, .0, length(q) - .45) * (1.12 - length(q - .1)) // rain
            + smoothstep(0., k,                                     // grid
                  min((r = abs(u) - .5, 0.), max(r.x, r.y)) + .05) * .3);


    // you can splain this?
    o = mix(o, vec4(.4, 0, 0, 0), smoothstep(.309, .309 + .5, cos(t)));
    o = mix(o, vec4(0, .4, 0, 0), smoothstep(.618, .618 + .5, cos(t)));
}
