#define PI 3.14
void Gabor(vec2 center, float G, float A, float B, float O, float R, float cont, float sf, vec2 uv, out vec3 col) {
    vec2 uv_o = 2.0 * (uv - center);
    float xp = uv_o.x * cos(A) - uv_o.y * sin(A);
    float yp = uv_o.x * sin(A) + uv_o.y * cos(A);
    float env = 2.0*exp(-((xp * xp) + (R * R * yp * yp)) / (2.0 * O * O));
    float car = cos(2.0 * PI * xp / (G * sf) + B);
    float gabor = env * car;
    col = cont * vec3(gabor);
}
void mainImage(out vec4 Ou, vec2 u) {
    vec2 Res = iResolution.xy,
    U = 4.0*(2.*u - Res) / Res.y; //https://www.shadertoy.com/view/Nlsczj (by: FabriceNeyret2)
    float x = 3.5*U.x, t = iTime;
    float G = 0.1;
    float A = t * 0.5;
    float B = 3.0;
    float O = 1.5;
    float R = 0.7;
    float cont = 1.0;
    float sf = 5.0;
    vec3 gabor1;
    Gabor(vec2(0.0, -0.1 + 0.2*sin(PI*2./3.*(x + 0.5*sin(PI/3.*(x + 3.*t))))), G*1.5, A*0.7, B, O*1.2, R, cont, sf*0.8, U, gabor1);
    Ou = vec4(0.875 * gabor1 + vec3(0,0,0), 1.0);
}
