#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p,q=vec3(-1.,.3,.1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=4.;
    p=R3D(iTime);
    for(j=0.;j++<6.;)
        p=abs(abs(p)-.03)-.05,
        h=clamp(dot(p,-p)/dot(p,q),-.2,1.)-.1;
        d=length(p-q*h*h)-.1;
    e+=d;
}o+=vec4(exp(q*q),1)*3./i;
}
