float box(vec2 b, vec2 uv){
    float left = step(0.3+b.x, uv.x);
    float bottom = step(0.3+b.y, uv.y);

    float right = step(0.3-b.x, 1.0-uv.x);
    float top = step(0.3-b.y, 1.0-uv.y);

    return float( (left * bottom) * ( right * top) );

}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv += 0.5;
    vec2 theMouse = vec2(iMouse) / iResolution.xy;

    fragColor = vec4(box(vec2(theMouse.x, theMouse.y), uv), 0.0, 0.0, 1.0);

}
