void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Coordenadas de pixel normalizadas (de 0 a 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Transformando as coordenadas de 0 a 1 em -0.5 a 0.5
    uv -= 0.5; 
    uv.x *= iResolution.x / iResolution.y;

    // Normalizando também as coordenadas do mouse para -0.5 a 0.5
    vec2 mouse = iMouse.xy / iResolution.xy - 0.5;
    mouse.x *= iResolution.x / iResolution.y;

    // Calcula a distância da posição do mouse com raio oscilante
    float time = iTime * 2.0;
    float radius = 0.3 + 0.1 * sin(time); // Raio pulsante

    vec2 offset = uv - mouse;
    float distance = length(offset);

    // Suavizando a cor da esfera
    float sphereFactor = smoothstep(radius, radius - 0.01, distance);

    // Deixando a esfera mais visível
    sphereFactor = 1.0 - sphereFactor;

    // Gradiente de cor na esfera
    vec3 sphereColor = mix(vec3(1.0, 1.0, 1.0), vec3(1.0, 0.0, 0.0), uv.y + 0.5);

    // Efeito de sombra na esfera
    float shadow = smoothstep(1.4, 0.0, distance);
    sphereColor *= shadow;

    // Brilho na esfera
    float highlight = smoothstep(1.1, 0.0, length(offset - vec2(0.1 * sin(time), 0.1 * cos(time))));
    sphereColor += vec3(1.0, 2.0, 1.0) * highlight * 0.5;

    // Background
    vec3 backgroundColor = mix(vec3(0.0, 0.1, 0.0), vec3(0.3, 0.3, 0.3), uv.y + 0.5);

    // Mistura final das cores
    vec3 finalColor = mix(backgroundColor, sphereColor, sphereFactor);

    // Saída para a tela
    fragColor = vec4(finalColor, 1.0);
}
