// -23 thanks to fabrðŸ§Š

#define M min(v.x, min(v.y, v.z))

void mainImage( out vec4 o, vec2 u )
{   
    vec3 R = iResolution, p, q, v,
         r = R - vec3(u+u, R.y);

    for (o *= 0.; o.a++ < 99.; q = p) 
        v = (fract(p*sign(-r) - 5e-5) + 5e-5) / abs(r),                        
        p += M * r,

        v = ceil((p+q)/2.),
        v.z += 45.,
        v.xz *= mat2(cos(vec4(0,11,33,0) + iTime)),

        abs(length(v = -abs(v)) - 27.) < .5 && M > -20. ?
            r /= r,
            p = q,
            o += .5 : o;
}
