void mainImage(out vec4 c, in vec2 v) {
    #define r(e) (94587u * e ^ e.wxyz)
    v = floor(v / 15.0);
    uvec4 i = uvec4(v, v + v), j = r(r(r(i / uvec4(3u))));
    c = vec4(i.x & i.y & 1u);
    c += vec4((i.x - 1u) & i.y & 1u | ((i.y - 1u) & i.x & 1u))
         * vec4(min(i.x, i.y) > 0u);
    if(min(i.x, i.y) > 0u && i.y * i.x > 2u) // Random disconnect.
        c -= vec4((i.x + 1u) & i.y & 1u) * vec4(j.y > j.x)
           + vec4((i.y + 1u) & i.x & 1u) * vec4(j.z > j.w);
    c += vec4(i.y == 1u && i.x == 8u);
    c.g += vec4(i.x == 2u && i.y == 2u).x;
    c.r += vec4(i.x == 82u && i.y == 46u).x;
    c += vec4(i.x == 82u && i.y == 47u).x;
}
