void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

vec2 field(vec2 coord){

    float x=coord.x;
    float y=coord.y;
    float a=-4.*x*(x*x+y*y-1.)+2.*y;
    float b=-4.*y*(x*x+y*y-1.)-2.*x;
    return vec2(a,b);
}


